mapper.insert:
insert into trending
    (
        repositorie_id, occur_time, time_code, language_code, rank, star, fork, new_star
    )
values
    (
     #{repositorieId}, #{occurTime}, #{timeCode}, #{languageCode}, #{rank}, #{star}, #{fork}, #{newStar}
    )

mapper.update:
update trending set
    rank = #{rank},
    star = #{star},
    fork = #{fork},
    new_star = #{newStar}
where id = #{id}

mapper.select:
select id, repositorie_id, occur_time, time_code, langulate_code, rank, star, fork, new_star
from trending


        time_code = #{timeCode}


        and language_code = #{languageCode}


        and occur_time = #{occurTime}


        and repositorie_id = #{repositorieId}
